package edward;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=2;
		switch(i)
		{
		case 1:
			System.out.println("the number is 1.");
			break;
			
		case 0:
			System.out.println("the number is 0.");
			break;
			
		default:
			System.out.println("the number is invalid.");
			break;
			
	
		}
		

	}

}
